import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Paths;
import java.util.LinkedList;

public class Main {

    /* Small function that reads a file
     * and returns it in a String.
     */
    static String readFile(String path) 
            throws IOException {
        byte[] encoded = Files.readAllBytes(Paths.get(path));
        return new String(encoded);
    }

    /* This function performs a benchmark of the Naive, Knuth-Morris-Pratt and Boyer-Moore algorithms,
    using all the texts and different types of patterns (short and long).
     */
    public static void main(String[] args) throws IOException {

        String[] filename = {"../data/verne.txt", "../data/tolstoy.txt", "../data/fibrobacter_succinogenes.txt",
                "../data/aspergillus_fumigatus.txt"};
        String[][] query = {{"île", "L'objet particulier de cette excursion était, on le sait, de"}, // verne.txt
                {"that", "Consider my position, Peter Nikolaevich. Were I in the cavalry I should"}, // tolstoy.txt
                // fibrobacter_succinogenes.txt
                {"TGCGGTCGCCTTCGGCAACCGAGCTACCGAACATGAGGTCCGCCTTTTCGGTGCGGACTTCACGCGTAA",
                ""},
                // aspergillus_fumigatus.txt
                {"ACACCAGAAGAGCGACGGGCGCGATGGCGTATATGAGATCGTGAGACCCCAGAAGCGGCACGGTGACGCGCAG",
                ""}};

        String text[] = new String[4];
        for (int i=0; i<4; i++) {
            text[i] = readFile(filename[i]);
        }

        // Naive algorithm
        long[][] NaiveTimes = new long[4][2];

        for (int i=0; i<4; i++) {
            for (int j=0; j<2; j++) {
                long startTime = System.nanoTime();
                Naive.naive(text[i], query[i][j]);
                NaiveTimes[i][j] = (System.nanoTime() - startTime)/1000000;
            }
        }

        String res = "Naive\n";
        for (int i=0; i<4; i++) {
            res += NaiveTimes[i][0] + " & " + NaiveTimes[i][1] + " \\\\ \n";
        }

        res += "\n\n";

        // Knuth-Morris-Pratt
        long[][] KMPTimes = new long[4][2];

        for (int i=0; i<4; i++) {
            for (int j=0; j<2; j++) {
                long startTime = System.nanoTime();
                KnuthMorrisPratt.kmp(text[i], query[i][j]);
                KMPTimes[i][j] = (System.nanoTime() - startTime)/1000000;
            }
        }

        res += "Knuth-Morris-Pratt\n";
        for (int i=0; i<4; i++) {
            res += KMPTimes[i][0] + " & " + KMPTimes[i][1] + " \\\\ \n";
        }

        res += "\n\n";

        // Boyer-Moore
        long[][] BMTimes = new long[4][2];

        for (int i=0; i<4; i++) {
            for (int j=0; j<2; j++) {
                long startTime = System.nanoTime();
                BoyerMoore.boyermoore(text[i], query[i][j]);
                BMTimes[i][j] = (System.nanoTime() - startTime)/1000000;
            }
        }

        res += "Boyer-Moore\n";
        for (int i=0; i<4; i++) {
            res += BMTimes[i][0] + " & " + BMTimes[i][1] + " \\\\ \n";
        }

        System.out.println(res);

    }

}
